class Accordion{constructor(){this.items=Array.from(document.querySelectorAll('.accordion__item'));this.firstItem=this.items[0];this.lastItem=this.items[this.items.length-1];this.keyCodes=Object.freeze({RETURN:13,ESC:27,SPACE:32,END:35,HOME:36,UP:38,DOWN:40});this.bindEvents()}
activatePanel(toggle){const parent=toggle.parentElement.parentElement;toggle.setAttribute('aria-expanded','true');parent.classList.add('accordion__item--expanded')}
deactivatePanel(toggle){const parent=toggle.parentElement.parentElement;toggle.setAttribute('aria-expanded','false');parent.classList.remove('accordion__item--expanded')}
handleClickEvent(event){const target=event.target.closest('.toggle');if(target&&target.getAttribute('aria-expanded')==='false'){this.activatePanel(target);return!0}
if(target&&target.getAttribute('aria-expanded')==='true'){this.deactivatePanel(target);return!0}}
handleKeydownEvent(event){const key=event.keyCode,target=event.target,listItem=target.parentElement.parentElement;let newTarget;switch(key){case this.keyCodes.DOWN:event.preventDefault();if(listItem!==this.lastItem){newTarget=listItem.nextElementSibling.querySelector('.toggle')}else{newTarget=this.firstItem.querySelector('.toggle')}
newTarget.focus();return!0;case this.keyCodes.UP:event.preventDefault();if(listItem!==this.firstItem){newTarget=listItem.previousElementSibling.querySelector('.toggle')}else{newTarget=this.lastItem.querySelector('.toggle')}
newTarget.focus();return!0;case this.keyCodes.HOME:event.preventDefault();newTarget=this.firstItem.querySelector('.toggle');newTarget.focus();return!0;case this.keyCodes.END:event.preventDefault();newTarget=this.lastItem.querySelector('.toggle');newTarget.focus();return!0;default:return!1}}
bindEvents(){document.addEventListener('click',this.handleClickEvent.bind(this));document.addEventListener('keydown',this.handleKeydownEvent.bind(this))}}
export default Accordion
